CustomAction.Code:
if (((bool)Settings["Mining"] &&
   (MiningLevel == MiningMaxLevel || ((bool)Settings["Herb"] && MiningMaxLevel > HerbMaxLevel ))) ||
 !(bool)Settings["Mining"])
{
    mining = Bots.Gatherbuddy.GatherbuddySettings.Instance.GatherMinerals = false;
}

if ((bool)Settings["Mining"] && MiningLevel != MiningMaxLevel && 
    ( MiningMaxLevel <= HerbMaxLevel || !(bool)Settings["Herb"] ))
{
   mining = Bots.Gatherbuddy.GatherbuddySettings.Instance.GatherMinerals = true;
}

if ((bool)Settings["Herb"] && HerbLevel != HerbMaxLevel &&
  (HerbMaxLevel <= MiningMaxLevel  || !(bool)Settings["Mining"] ) )
{
   herb = Bots.Gatherbuddy.GatherbuddySettings.Instance.GatherHerbs = true;
}

using (StyxWoW.Memory.AcquireFrame())
{
  foreach (var node in ObjectManager.GetObjectsOfType<WoWGameObject>().Where( g => (g.IsMineral && (bool)Settings["Mining"] || g.IsHerb && (bool)Settings["Herb"]) && !Blacklist.Contains(g)))
  {
    var requiredSkillLevel = node.RequiredSkill;
    if (!requiredSkillLevel.HasValue) continue;
    var skillLevel =  node.IsHerb ? HerbLevel : MiningLevel;
    if (requiredSkillLevel.Value + 150 < skillLevel)
    {
          Blacklist.Add(node, TimeSpan.FromDays(365));
           MyLog("Blacklisting {0} because it's grey", node.Name);
          MyLog("My Skill: {0}, Required Skill: {1}",skillLevel ,requiredSkillLevel.Value);
    } 
  }
}

Declaration.Code:
void Quit(string reason,params object[] args)
{
  MyLog(reason,args);
  Bots.Gatherbuddy.GatherbuddySettings.Instance.GatherMinerals = true;
  Bots.Gatherbuddy.GatherbuddySettings.Instance.GatherHerbs = true;
   Lua.DoString("Logout()");
  TreeRoot.Stop();
}

void MyLog(string format, params object[] args)
{
    Log(Colors.Green,"Mine/Herb 1-600: ",Colors.LightGreen,format, args);
}
